definition.script:
node {
    stage('SCM') {
        git 'https://github.com/ArieShout/sample-acs-k8s-configs.git'
    }

    stage('Deploy') {
        kubernetesDeploy configs: 'src/main/kubernetes/nginx.yml',
            credentialsType: 'SSH',
            ssh: [sshCredentialsId: 'ssh-credential', sshServer: '$$k8s-master-host$$']
    }
}
